import React, { useState } from 'react';
import axios from 'axios';

function PostForm(){
    const url= "http://localhost:8080/order/submit"; //Get Url from Arjun and add it here
    const [data, setData] = useState({
        target: "",
        videoUrl: ""
    })

    function submit(e){
        e.preventDefault();
        axios.post(url, {
            target: data.target,
            videoUrl: data.videoUrl
        }).then(res=>{
            console.log(res.data)
        })
    }

    function handle(e){
        const newdata={...data}
        newdata[e.target.id] = e.target.value
        setData(newdata)
        console.log(newdata)
    }

    const formStyle = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        gap: '15px',
        maxWidth: '300px',
        margin:'50px auto',
        padding: '20px',
        boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
        borderRadius:'8px',
        backgroundColor: '#add8e6'
    }

    return (
        <form onSubmit={(e)=> submit(e)} style={formStyle}>
            <text>For Target:</text>
            <text>For Audience: 0</text>
            <text>For Speaker: 1</text>
            <text>For Both: 2</text>
            <input onChange={(e) => handle(e)} id="target" value={data.target} placeholder="Target Audience" type="number"></input>
            <input onChange={(e) => handle(e)} id="videoUrl" value={data.videoUrl} placeholder="Video Url" type="text"></input>
            <button>Submit</button>
        </form>
    );
}

export default PostForm;
