import React, { useState } from 'react';
import axios from 'axios';

function PostForm(){
    const url= "http://localhost:8080/order/submit"; //Get Url from Arjun and add it here
    const [data, setData] = useState({
        target: "",
        videoUrl: ""
    })

    function submit(e){
        e.preventDefault();
        axios.post(url, {
            target: data.target,
            videoUrl: data.videoUrl
        }).then(res=>{
            console.log(res.data)
        })
    }

    function handle(e){
        const newdata={...data}
        newdata[e.target.id] = e.target.value
        setData(newdata)
        console.log(newdata)
    }

    const formStyle = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        gap: '15px',
        margin: '50px auto',
        maxWidth: '750px',
        padding: '50px',
        boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
        borderRadius:'8px',
        backgroundColor: 'white'
    }

    const inputStyle = {
        width: '50%',
        padding: '10px',
        boxSizing: 'border-box',
        borderRadius: '1px',
        border: '1px solid #ccc'
    };

    const h1Style = {
        margin: '2px'
    }

    const h3Style ={
        margin: '2px'
    }


    return (
        <form onSubmit={(e)=> submit(e)} style={formStyle}>
            <h1 style = {h1Style}>Video Submission and Analysis Form</h1>
            <h3 style = {h3Style}>For Target Audience:</h3>
            <text>For Audience: Type 0</text>
            <text>For Speaker: Type 1</text>
            <text>For Both: Type 2</text>
            <input onChange={(e) => handle(e)} id="target" value={data.target} placeholder="Target Audience" type="number" style = {inputStyle}></input>
            <input onChange={(e) => handle(e)} id="videoUrl" value={data.videoUrl} placeholder="Video Url" type="text" style = {inputStyle}></input>
            <button>Submit</button>
        </form>
    );
}

export default PostForm;
